import React from 'react';
import { FaBed, FaBath } from 'react-icons/fa';
import { BiArea } from 'react-icons/bi';
import { MdLocationOn } from 'react-icons/md';

const PropertyCard = ({ property }) => {
  const {
    image,
    title,
    location,
    price,
    bedrooms,
    bathrooms,
    area
  } = property;

  // Format price to USD currency
  const formattedPrice = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    maximumFractionDigits: 0
  }).format(price);

  return (
    <div className="bg-white rounded-lg overflow-hidden shadow-sm hover:shadow-md transition-shadow">
      {/* Property Image */}
      <div className="relative h-48">
        <img
          src={image}
          alt={title}
          className="w-full h-full object-cover"
        />
      </div>

      {/* Property Details */}
      <div className="p-4">
        <h3 className="text-xl font-semibold text-gray-900 mb-2">
          {title}
        </h3>
        
        <div className="flex items-center text-gray-600 mb-4">
          <MdLocationOn className="w-4 h-4 mr-1" />
          <span className="text-sm">{location}</span>
        </div>

        {/* Property Features */}
        <div className="flex items-center gap-4 mb-4 text-gray-600">
          <div className="flex items-center">
            <FaBed className="w-4 h-4 mr-1" />
            <span className="text-sm">{bedrooms} Beds</span>
          </div>
          <div className="flex items-center">
            <FaBath className="w-4 h-4 mr-1" />
            <span className="text-sm">{bathrooms} Baths</span>
          </div>
          <div className="flex items-center">
            <BiArea className="w-4 h-4 mr-1" />
            <span className="text-sm">{area} ftÂ²</span>
          </div>
        </div>

        {/* Price */}
        <div className="flex items-center justify-between">
          <span className="text-xl font-bold text-[#00513B]">
            {formattedPrice}
          </span>
          <button className="px-4 py-2 bg-[#00513B] text-white rounded-lg hover:bg-[#003D2D] transition-colors">
            View Details
          </button>
        </div>
      </div>
    </div>
  );
};

export default PropertyCard;
