import React, { useState } from 'react';
import { FiSliders } from 'react-icons/fi';
import PropertyCard from '../molecules/PropertyCard';

/**
 * PropertyList Component
 * 
 * Displays a grid of property listings with filtering and load more functionality.
 * 
 * Features:
 * - Responsive grid layout
 * - Property filtering by type
 * - Load more pagination
 * - Property card display with images and details
 * 
 * Props:
 * - properties: Array of property objects containing listing details
 * - filters: Array of available filter options
 * 
 * State:
 * - visibleProperties: Number of properties currently displayed
 * - selectedFilter: Currently active filter
 */
function PropertyList() {
  const [visibleProperties, setVisibleProperties] = useState(6);
  
  const properties = [
    {
      id: 1,
      image: "/assets/images/image.png",
      title: "Luxury Apartment in the\nHeart of Damascus",
      location: "Abu Rummaneh, Damascus",
      size: "180 m²",
      price: "45k USD"
    },
    {
      id: 2,
      image: "/assets/images/image (1).png",
      title: "Modern Villa with\nPrivate Pool",
      location: "Yaafour, Damascus",
      size: "350 m²",
      price: "120k USD"
    },
    {
      id: 3,
      image: "/assets/images/image (2).png",
      title: "Spacious Office Space\nin Business District",
      location: "Mezzeh, Damascus",
      size: "220 m²",
      price: "75k USD"
    },
    {
      id: 4,
      image: "/assets/images/image (3).png",
      title: "Cozy Studio in\nCity Center",
      location: "Malki, Damascus",
      size: "90 m²",
      price: "35k USD"
    },
    {
      id: 5,
      image: "/assets/images/image (4).png",
      title: "Penthouse with\nPanoramic View",
      location: "Abu Rummaneh, Damascus",
      size: "250 m²",
      price: "95k USD"
    },
    {
      id: 6,
      image: "/assets/images/image (5).png",
      title: "Family Home with\nGarden",
      location: "Kafar Sousah, Damascus",
      size: "320 m²",
      price: "85k USD"
    },
    {
      id: 7,
      image: "/assets/images/image (6).png",
      title: "Commercial Space\nfor Business",
      location: "Downtown, Damascus",
      size: "150 m²",
      price: "65k USD"
    },
    {
      id: 8,
      image: "/assets/images/image (7).png",
      title: "Modern Loft\nApartment",
      location: "West Mezzeh, Damascus",
      size: "140 m²",
      price: "55k USD"
    },
    {
      id: 9,
      image: "/assets/images/image (8).png",
      title: "Luxury Villa with\nPool View",
      location: "Yaafour, Damascus",
      size: "400 m²",
      price: "150k USD"
    },
    {
      id: 10,
      image: "/assets/images/image.png",
      title: "Duplex Apartment\nwith Terrace",
      location: "Malki, Damascus",
      size: "200 m²",
      price: "80k USD"
    },
    {
      id: 11,
      image: "/assets/images/image (1).png",
      title: "Office Space with\nParking",
      location: "Mezzeh, Damascus",
      size: "180 m²",
      price: "70k USD"
    },
    {
      id: 12,
      image: "/assets/images/image (2).png",
      title: "Garden Apartment\nwith Pool Access",
      location: "Abu Rummaneh, Damascus",
      size: "160 m²",
      price: "60k USD"
    }
  ];

  const handleLoadMore = () => {
    setVisibleProperties(prev => Math.min(prev + 3, properties.length));
  };

  return (
    <div>
      <div className="flex items-baseline gap-6">
        <h2 className="text-5xl md:text-6xl font-bold text-[#00513B]">New offers</h2>
        <span className="text-gray-600 text-base">Choose from the new offers what suits you</span>
      </div>

      {/* Property Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mt-8 mb-8">
        {properties.slice(0, visibleProperties).map(property => (
          <PropertyCard
            key={property.id}
            image={property.image}
            title={property.title}
            location={property.location}
            size={property.size}
            price={property.price}
          />
        ))}
      </div>

      {/* Load More Button */}
      {visibleProperties < properties.length && (
        <div className="flex justify-center">
          <button
            onClick={handleLoadMore}
            className="bg-white text-gray-600 px-8 py-2 rounded-full shadow-sm hover:shadow-md transition-shadow"
          >
            Load more
          </button>
        </div>
      )}
    </div>
  );
}

export default PropertyList;
