import React, { useState, useCallback, useMemo } from 'react';
import PropertyCard from '../molecules/PropertyCard';
import { FiFilter } from 'react-icons/fi';

/**
 * PropertyList Component
 * 
 * Displays a grid of property listings with filtering and load more functionality.
 * 
 * Features:
 * - Responsive grid layout
 * - Property filtering by type
 * - Load more pagination
 * - Property card display with images and details
 * 
 * Props:
 * - properties: Array of property objects containing listing details
 * - filters: Array of available filter options
 * 
 * State:
 * - visibleProperties: Number of properties currently displayed
 * - selectedFilter: Currently active filter
 */

// Mock properties data
const properties = [
  {
    id: 1,
    title: "Modern Villa with Pool",
    type: "villa",
    price: 450000,
    bedrooms: 4,
    bathrooms: 3,
    location: "Miami, FL",
    area: 2500,
    image: "/assets/properties/villa1.jpg"
  },
  {
    id: 2,
    title: "Downtown Apartment",
    type: "apartment",
    price: 180000,
    bedrooms: 2,
    bathrooms: 1,
    location: "New York, NY",
    area: 1200,
    image: "/assets/properties/apartment1.jpg"
  },
  {
    id: 3,
    title: "Suburban Family House",
    type: "house",
    price: 320000,
    bedrooms: 3,
    bathrooms: 2,
    location: "Los Angeles, CA",
    area: 1800,
    image: "/assets/properties/house1.jpg"
  },
  {
    id: 4,
    title: "Luxury Penthouse",
    type: "apartment",
    price: 550000,
    bedrooms: 3,
    bathrooms: 2,
    location: "Chicago, IL",
    area: 2000,
    image: "/assets/properties/penthouse1.jpg"
  },
  {
    id: 5,
    title: "Beachfront Villa",
    type: "villa",
    price: 750000,
    bedrooms: 5,
    bathrooms: 4,
    location: "San Diego, CA",
    area: 3500,
    image: "/assets/properties/villa2.jpg"
  },
  {
    id: 6,
    title: "Cozy Studio Apartment",
    type: "apartment",
    price: 95000,
    bedrooms: 1,
    bathrooms: 1,
    location: "Seattle, WA",
    area: 600,
    image: "/assets/properties/studio1.jpg"
  }
];

// Memoized filter options
const filterOptions = {
  priceRanges: [
    { label: 'Under $100k', value: '0-100000' },
    { label: '$100k - $200k', value: '100000-200000' },
    { label: '$200k - $500k', value: '200000-500000' },
    { label: 'Over $500k', value: '500000+' }
  ],
  propertyTypes: [
    { label: 'All Types', value: 'all' },
    { label: 'Houses', value: 'house' },
    { label: 'Apartments', value: 'apartment' },
    { label: 'Villas', value: 'villa' }
  ],
  bedrooms: [
    { label: 'Any', value: 'any' },
    { label: '1+', value: '1' },
    { label: '2+', value: '2' },
    { label: '3+', value: '3' }
  ]
};

function PropertyList() {
  const [visibleCount, setVisibleCount] = useState(6);
  const [selectedFilters, setSelectedFilters] = useState({
    priceRange: 'all',
    propertyType: 'all',
    bedrooms: 'any'
  });
  const [showFilters, setShowFilters] = useState(false);

  // Memoized filtered properties
  const filteredProperties = useMemo(() => {
    return properties.filter(property => {
      if (selectedFilters.priceRange !== 'all') {
        const [min, max] = selectedFilters.priceRange.split('-').map(Number);
        if (max) {
          if (property.price < min || property.price > max) return false;
        } else {
          if (property.price < min) return false;
        }
      }
      if (selectedFilters.propertyType !== 'all' && property.type !== selectedFilters.propertyType) return false;
      if (selectedFilters.bedrooms !== 'any' && property.bedrooms < Number(selectedFilters.bedrooms)) return false;
      return true;
    });
  }, [selectedFilters]);

  const handleLoadMore = useCallback(() => {
    setVisibleCount(prev => prev + 6);
  }, []);

  const handleFilterChange = useCallback((filterType, value) => {
    setSelectedFilters(prev => ({ ...prev, [filterType]: value }));
    setVisibleCount(6); // Reset visible count when filters change
  }, []);

  return (
    <section className="py-12 bg-gray-50">
      <div className="container mx-auto px-4">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-8">
          <h2 className="text-3xl font-bold text-gray-900 mb-4 md:mb-0">
            Featured Properties
          </h2>
          <button
            onClick={() => setShowFilters(!showFilters)}
            className="flex items-center gap-2 px-4 py-2 bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow"
            aria-label="Toggle filters"
          >
            <FiFilter className="w-5 h-5" />
            <span>Filters</span>
          </button>
        </div>

        {/* Filters */}
        {showFilters && (
          <div className="bg-white p-4 rounded-lg shadow-sm mb-8">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Price Range</label>
                <select
                  value={selectedFilters.priceRange}
                  onChange={(e) => handleFilterChange('priceRange', e.target.value)}
                  className="w-full rounded-md border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                >
                  {filterOptions.priceRanges.map(option => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Property Type</label>
                <select
                  value={selectedFilters.propertyType}
                  onChange={(e) => handleFilterChange('propertyType', e.target.value)}
                  className="w-full rounded-md border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                >
                  {filterOptions.propertyTypes.map(option => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Bedrooms</label>
                <select
                  value={selectedFilters.bedrooms}
                  onChange={(e) => handleFilterChange('bedrooms', e.target.value)}
                  className="w-full rounded-md border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500"
                >
                  {filterOptions.bedrooms.map(option => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                  ))}
                </select>
              </div>
            </div>
          </div>
        )}

        {/* Property Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredProperties.slice(0, visibleCount).map(property => (
            <PropertyCard key={property.id} property={property} />
          ))}
        </div>

        {/* Load More Button */}
        {visibleCount < filteredProperties.length && (
          <div className="text-center mt-8">
            <button
              onClick={handleLoadMore}
              className="px-6 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors"
            >
              Load More
            </button>
          </div>
        )}
      </div>
    </section>
  );
}

export default PropertyList;
